/* eslint-disable @next/next/no-img-element */
import Link from 'next/link'
import { useRouter } from 'next/router'
import React, { useState } from 'react'

const Navbar = () => {
	const router = useRouter();
	const [showDropDown, setShowDropDown] = useState(false);
	const [showMenu, setShowMenu] = useState(false)
	const handelEnglish = () =>{
		router.push('/english-caption');
		setShowDropDown(showDropDown = false);
		setShowMenu(showMenu = false);
	};
	const handelHindi = () =>{
		router.push('/hindi-caption');
		setShowDropDown(showDropDown = false);
		setShowMenu(showMenu = false);
	};
	const handelPunjabi = () =>{
		router.push('/punjabi-caption');
		setShowDropDown(showDropDown = false);
		setShowMenu(showMenu = false);
	};
  return (
	<>
		<div className='sticky top-0 bg-[#ffffff]'>
			<nav className='hidden lg:flex justify-between py-2 mx-20 '>
				<Link href='/'>
				<h1 className='text-3xl text-[#f9748f] hover:cursor-pointer flex'>
					<img className='h-6 mt-2 mr-2' src="data:image/png;base64," alt="" />
					IGCaption
				</h1>
				</Link>
				<ul className='flex space-x-16 my-auto'>
					<Link className='' href='/'>
						<li className={router.pathname == '/' ? "flex text-lg hover:cursor-pointer underline underline-offset-4" : "flex text-lg hover:cursor-pointer"}>
							<span className='mr-2'><img className='h-5 my-auto mt-1' src="data:image/png;base64," alt="" /></span>
							Home
						</li>
					</Link>
					<button onClick={() => setShowDropDown(!showDropDown)}>
						<li className="flex text-lg hover:cursor-pointer">
							<span className='mr-2'><img className='h-5 my-auto mt-1' src="data:image/png;base64," alt="" /></span>
							Caption
							{showDropDown ? (
								<span className=''><img className='h-3 my-auto mt-3' src="data:image/png;base64," alt="" /></span>
							):(
								<span className=''><img className='h-3 my-auto mt-3' src="data:image/png;base64," alt="" /></span>
							)}
						</li>
					</button>
				</ul>
			</nav>

			{/* dropdown for large device */}

			<div className={showDropDown ? 'hidden lg:flex justify-end mr-20' : 'hidden'}>
				<div className='absolute bg-[#ffffff] mt-2 rounded-md'>
					<ul className='mx-1 my-4 flex-col space-y-4'>
						<li onClick={handelEnglish} className='px-6 w-full flex py-2 hover: cursor-pointer hover:bg-[#eeeeee]'>
							<img className='h-5 mr-2' src="data:image/png;base64," alt="" />
							English Caption
						</li>
						<li onClick={handelHindi} className='px-6 w-full flex py-2 hover: cursor-pointer hover:bg-[#eeeeee]'>
							<img className='h-5 mr-2' src="data:image/png;base64," alt="" />
							Hindi Caption
						</li>
						<li onClick={handelPunjabi} className='px-6 w-full flex py-2 hover: cursor-pointer hover:bg-[#eeeeee]'>
							<img className='h-5 mr-2' src="data:image/png;base64," alt="" />
							Punjabi Caption
						</li>
					</ul>
				</div>
			</div>

			{/* navbar for small device */}

			<nav className='flex justify-between py-4 mx-5 md:mx-8 lg:hidden '>
				<Link href='/'>
					<img className='h-8 md:h-12 my-auto mt-2 hover:cursor-pointer' src="data:image/png;base64," alt="" />
				</Link>
				<button onClick={() => setShowMenu(!showMenu)}>
				<img className='h-8 md:h-12 my-auto mt-2 hover:cursor-pointer' src={showMenu ? "data:image/png;base64," : "data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAEAAAABAAgMAAADXB5lNAAAAA3NCSVQICAjb4U/gAAAACXBIWXMAAAJNAAACTQGIb/sZAAAAGXRFWHRTb2Z0d2FyZQB3d3cuaW5rc2NhcGUub3Jnm+48GgAAAAxQTFRF////AAAAAAAAAAAA+IwCTQAAAAN0Uk5TADzJaz3HNQAAAC5JREFUOMtjYBgFKCD/Pwr4ybAfVeAfpgCGllHfDgkvQ52+H8lz2AXyh65vaQ0Ac96mS4/iyEkAAAAASUVORK5CYII="} alt="" />
				</button>
			</nav>

			{/* menu for large device */}
			<div className={showMenu ? 'flex justify-end mr-5 lg:hidden' : 'hidden'}>
				<div className='absolute bg-[#ffffff] mt-2 rounded-md'>
					<ul className='mx-1 my-4 flex-col space-y-4'>
						<li onClick={handelEnglish} className='px-6 w-full flex py-2 hover: cursor-pointer hover:bg-[#eeeeee]'>
							<img className='h-5 mr-2' src="data:image/png;base64," alt="" />
							English Caption
						</li>
						<li onClick={handelHindi} className='px-6 w-full flex py-2 hover: cursor-pointer hover:bg-[#eeeeee]'>
							<img className='h-5 mr-2' src="data:image/png;base64," alt="" />
							Hindi Caption
						</li>
						<li onClick={handelPunjabi} className='px-6 w-full flex py-2 hover: cursor-pointer hover:bg-[#eeeeee]'>
							<img className='h-5 mr-2' src="data:image/png;base64," alt="" />
							Punjabi Caption
						</li>
					</ul>
				</div>
			</div>


		</div>
	</>
  )
}

export default Navbar